import { Component } from "react"
import Cuerpo from "./cuerpo/cuerpo"
import Encabezado from "./encabezado"
import axios from "../conexion";

class Principal extends Component{
    constructor (props){
        super(props);
        this.state={
            ingresos: 0,
            egresos: 0,
            total: this.Ingresos-this.Egresos
        }
    }

    componentDidMount= async()=>{
        var config = {
            method: 'get',
            url: 'http://localhost:3200/api/operation/suma/'+ this.props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
        };
        const operation= await axios(config);
        var data =operation.data;
        for (let index = 0; index < 2; index++) {
            if (data[index].tipo==='Ingreso'  ){
                this.setState({ingresos: data[index].monto})
            }  if (data[index].tipo==='Egreso'  ){
                this.setState({egresos: data[index].monto})
            }  
        }
    }

    render(){
        return(
            <div>
                <Encabezado Total={this.state.total}/>
                <Cuerpo id= {this.props.id}/>
            </div>
        )
    }
}

export default Principal;
