import { useState,useEffect } from 'react';
import axios from '../conexion';

function Encabezado (props){
    var [suma,setsuma]= useState(0);
    var [datos,setdatos]= useState([]);

    useEffect(()=>{
        cargar()
    })
    
    const cargar= async ()=>{
        var config = {
            method: 'get',
            url: 'http://localhost:3200/api/operation/suma/'+ props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
        };
        const sum= await axios(config)
        setdatos(sum.data)
        for (let i=0;i++;datos.length){
            if (datos[i].tipo===''){
                setsuma(suma+datos[i].monto)
            }
        }
        console.log(datos)
    }

    return(
        <div className="Principal">
            <h1>Presupuesto Personal</h1>
            <h2>Su saldo {suma}</h2>
        </div>
    )
}

export default Encabezado;
