import { Component } from "react";
import Egresos from "./egresos/egresos";
import Ingresos from "./ingresos/ingresos";
import Listado from "./listado/listado";
import Nuevos from "./nuevos/nuevos";

class Cuerpo extends Component{
    constructor(props){
        super(props);
        this.state={
            change: false
        }
    }

    modificar= (operaciones)=>{
        this.setState({
            id_oper: operaciones.id_oper,
            concepto: operaciones.concepto,
            monto: operaciones.monto,
            tipo: operaciones.tipo,
            fecha: operaciones.fecha,
            categoriumIdCategoria: operaciones.categoriumIdCategoria
        });
        
    }

    render(){
        return(
            <div>
                <div className="Principal">
                    <Listado id= {this.props.id} modificar={this.modificar}/>
                    <div className='Modificar'>
                        <h1>Modificar {this.state.id_oper} de fecha {this.state.fecha}</h1>
                        concepto: <input value={this.state.concepto} onChange={e => this.modificar({...this.state, concepto: e.target.value})}></input> <br/>
                        monto: <input value={this.state.monto} onChange={e => this.modificar({...this.state, monto: e.target.value})}></input> <br/>
                        fecha: <input type='date'></input> <br/>
                        <button>Aceptar</button>
                        <button>Cerrar</button>
                    </div>
                    <Nuevos id={this.props.id} />
                </div>
                <div >
                    <Ingresos id= {this.props.id}/>
                    <Egresos id= {this.props.id}/>
                </div>
            </div>
        )
    }
}

export default Cuerpo;
