import { Component } from "react"
import Cuerpo from "./cuerpo/cuerpo"
import Encabezado from "./encabezado"
import axios from '../conexion';


class Principal extends Component{
    constructor(props){
        super(props);
        this.state={
            obj:[],
            suma:0
        } 
    }

    componentDidMount(){
        var sum= this.calcular()
        this.setState({obj:sum.data})
        for (let i=0;i++;3){
            if (sum[i].tipo==='Ingreso'){
                this.suma=this.suma+this.obj[i].monto;
            }
            else{
                this.suma=this.suma-this.obj[i].monto;
            }
        }
        console.log(this.obj)
    }

    calcular= async ()=>{
        var config = {
            method: 'get',
            url: 'http://localhost:3200/api/operation/suma/'+ this.props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
        };
        await axios(config)
    }

    render(){
        return(
            
            <div>
                <Encabezado id= {this.props.id} />
                <Cuerpo id= {this.props.id}/>
            </div>
        )
    }
}

export default Principal;
