import { Component } from "react"
import Cuerpo from "./cuerpo/cuerpo"
import Encabezado from "./encabezado"
import axios from "../conexion"


class Principal extends Component{
    constructor(props){
        super(props);
        this.state={
            a:0,
            b:0,
            suma:0
        }
    }

    async componentDidMount(){
        var config = {
            method: 'get',
            url: 'http://localhost:3200/api/operation/Ingreso/'+ this.props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
        }
        var ingreso= await axios(config);
        this.setState({a:ingreso.data[0]["monto"]});

        config = {
            method: 'get',
            url: 'http://localhost:3200/api/operation/Egreso/'+ this.props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
        }
        var egreso= await axios(config)
        this.setState({b:egreso.data[0]["monto"]});

        let sum= ingreso-egreso
        this.setState({suma: sum.toFixed(2)})
    }
    

    render(){
        return(
            
            <div>
                <Encabezado id= {this.props.id} sum={suma}/>
                <Cuerpo id= {this.props.id} ingreso={a} ingreso={b}/>
            </div>
        )
    }
}

export default Principal;
