import axios from '../../../conexion';
var qs = require('qs');

const Nuevos =(props)=>{
    const cargar= async (concepto, monto, fecha, tipo, categoria)=>{
        var data = qs.stringify({
            'concepto': concepto,
            'monto': monto,
            'fecha':fecha,
            'tipo':tipo,
            'categoria': categoria
          });

        var config = {
            method: 'put',
            url: 'http://localhost:3200/api/operation/'+ props.id,
            headers: { 
              'Content-Type': 'application/x-www-form-urlencoded'
            },
            data: data
        };
        const operation= await axios(config)
        
    }

    return(
        <div>
            <h2>Nuevos</h2>
            <form>
                Tipo:
                <select id='select'>
                    <option>Ingreso</option>
                    <option>Egreso</option>
                </select>
                concepto: <input id='concepto'></input>
                monto: <input id='monto'></input>
                fecha: <input id='fecha'></input>
                categoria:
            </form>
            <button onClick={()=>{cargar(document.getElementById('select').text)}}>Cargar</button>
        </div>
    )
}

export default Nuevos;
